SELECT i.film_id filmId, f.title title, i.store_id storeId, COUNT(*) total FROM inventory i LEFT JOIN film f ON i.film_id = f.film_id GROUP BY i.film_id, i.store_id
HAVING f.title LIKE CONCAT('%', #{searchWord}, '%')
LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM (SELECT i.film_id filmId, f.title title, i.store_id storeId, COUNT(*) total FROM inventory i LEFT JOIN film f ON i.film_id = f.film_id GROUP BY i.film_id, i.store_id
HAVING f.title LIKE CONCAT('%', #{searchWord}, '%')
) s
